package tictactoe.brain;

// TODO: Auto-generated Javadoc
/**
 * The Class Constants.
 */
public class Constants {
	
	/** The Constant boardSize. */
	public final static int boardSize = 4;
	
	/** The Constant winningSize - the minimum size of the winning line */
	public final static int winningSize = 3;
	
	/** The Constant x. In the matrix, 1 is used to mark that an X is in that position */
	public final static int x = 1;
	
	/** The Constant used for winningX. In the matrix, 11 is used to mark that an X is in that position 
	 * and on a winning line
	 * */
	public final static int winningX = 11;
	
	/** The Constant o. In the matrix, 0 is used to mark that an O is in that position*/
	public final static int o = 0;
	
	/** The Constant used for winningO. In the matrix, 10 is used to mark that an O is in that position
	 * and on a winning line
	 * */
	public final static int winningO = 10;
	
	/** The Constant free. In the matrix, -1 is used to mark a empty position*/
	public final static int free = -1;
	
	/** The Constant cellWidth. */
	public final static int cellWidth = 200;
	
	/** The Constant gameInProgress. */
	public final static int gameInProgress = -2;
	
	/** The Constant noWinner. */
	public final static int noWinner = -1;
	

}
